mapper.select:
SELECT MEMBER_ID
  FROM TB_MEMBER
 WHERE MEMBER_NAME = #{ name }
   AND EMAIL = #{ email }
   AND MEMBER_STATUS = 'Y'

SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE MEMBER_ID = #{ id }
   AND EMAIL = #{ email }

SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE EMAIL = #{ email }

mapper.insert:
INSERT INTO TB_MEMBER
(
  MEMBER_ID
, MEMBER_PWD
, MEMBER_NAME
, EMAIL
, BIRTHDAY
, GENDER
)
VALUES
(
  #{ id }
, #{ pwd }
, #{ name }
, #{ email }
, #{ birthday }
, #{ gender }
)

mapper.update:
UPDATE
	   TB_MEMBER
   SET MEMBER_NAME = #{ name }
     , EMAIL = #{ email }
     , BIRTHDAY = #{ birthday }
     , GENDER = #{ gender }
 WHERE MEMBER_ID = #{ id }
   AND MEMBER_STATUS = 'Y'

mapper.select:
SELECT A.RNUM , A.MEMBER_TYPE , A.MEMBER_ID FROM (SELECT ROWNUM RNUM , B.MEMBER_TYPE , B.MEMBER_ID FROM (SELECT C.MEMBER_TYPE , C.MEMBER_ID FROM TB_MEMBER C WHERE MEMBER_TYPE NOT IN ('관리자') AND MEMBER_STATUS = 'Y'
AND MEMBER_ID LIKE '%' || #{ searchValue } || '%'
) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

mapper.update:
UPDATE TB_MEMBER
SET MEMBER_PWD = #{ pwd }
WHERE MEMBER_ID = #{ id }
AND MEMBER_STATUS = 'Y'
